if (msg.payload.state === undefined || 
    msg.payload.state.buttonevent === undefined) {
    return null;
}

var event = msg.payload.state.buttonevent;
var side = Math.round(event/1000);
var modifier = event - (side * 1000);
msg.side = side;

if (event % 1000 === 0) {
    msg.event = "slide";
}
else if (event === 7007) {
    msg.event = "shake";
}
else if (event === (side*1000) + side) {
    msg.event = "doubletap";
}
else if (side + modifier === 7) {
    msg.event = "180flip";
}
else if (side != 7 - modifier) {
    msg.event = "flip";
}
else if (event === 7008) {
    msg.event = "drop";
}
else {
    msg.event = "unknown (" + event + ")";
}
return msg;

