var socketname = msg.payload
var state = flow.get(socketname) || 0;
var event = msg.event;
if (state === 1 && (event === undefined || event == "doubletap")) {
    flow.set(socketname, 0);
    msg.payload = "$OFF";
}
else if (state === 0  && (event === undefined || event == "shake")) {
    flow.set(socketname, 1);
    msg.payload = "$ON";
}

msg.topic = "/device/smartsocket/" + socketname + "/command";
return msg;

if (msg.payload.state === undefined || 
    msg.payload.state.buttonevent === undefined) {
    return null;
}

var event = msg.payload.state.buttonevent;
var side = Math.round(event/1000);
var modifier = event - (side * 1000);
msg.side = side;

if (event % 1000 === 0) {
    msg.event = "slide";
}
else if (event === 7007) {
    msg.event = "shake";
}
else if (event === (side*1000) + side) {
    msg.event = "doubletap";
}
else if (side + modifier === 7) {
    msg.event = "180flip";
}
else if (side != 7 - modifier) {
    msg.event = "flip";
}
else if (event === 7008) {
    msg.event = "drop";
}
else {
    msg.event = "unknown (" + event + ")";
}
return msg;

